export default function ProductNavItem (props){}
    const { children, to, className, activeClassName } = props
    const { state, dispatch } = useContext(StoreContext);
    const getJSON = url => {
        switch (url) {
           case "/motherday":
              return motherday;
           case "/sale":
              return sale;
           case "/egg":
              return egg;
           case "/cake":
              return cake;
           case "/jelly":
              return jelly;
           default:
              return products;
        }
        const onClick = () => {
            console.log(children)
            dispatch({
               type: SET_PAGE_TITLE, 
               payload: children,
            });
            dispatch({
               type: SET_PAGE_CONTENT, 
               payload: getJSON(to),
            });
            dispatch({
               type: SET_NAVBAR_ACTIVEITEM, 
               payload: to,
            });
         };
}
